mapper.select:


    ro.deleted = 0
     and ro.order_no like concat('%', #{orderNo}, '%')
     and ro.user_id = #{userId}
     and ro.user_name like concat('%', #{userName}, '%')
     and ro.house_id = #{houseId}
     and ro.repair_type = #{repairType}
     and ro.urgency_level = #{urgencyLevel}
     and ro.order_status = #{orderStatus}
     and ro.worker_id = #{workerId}
     and ro.worker_name like concat('%', #{workerName}, '%')

        and date_format(ro.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


        and date_format(ro.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')


order by ro.create_time desc
